	main() {
		console.log("==== main() start ====");
		storage.login().then(() => {
			console.log("==== logged in ====");
			renderEngine.render(this.river_);
			$("#btn_save").addClass("getsDirty");
			$("#btn_rename").addClass("getsDirty");
